    private void jbtnProcesaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbtnProcesaActionPerformed
        
       
        
        
       clsPagar oValorPago=new clsPagar();
       
       String pago =(String) jcbTipo.getSelectedItem();
       
        jLblPago.setText(String.valueOf(oValorPago.total(Integer.valueOf(jtxtCantidad.getText()), Integer.valueOf(jtxtPrecio.getText()),pago)));
       
       
        
    }//GEN-LAST:event_jbtnProcesaActionPerformed

